import { Component } from '@angular/core';
import { FormControl, FormGroup, Validators } from '@angular/forms';
import { Router } from '@angular/router';


interface Task {
  done: boolean;
  description: string;
}

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent {
  taskList: Task[] = []
  signupForm: FormGroup;

  constructor(private router: Router) {
    this.taskList = [
      {done: false, description: "Clean the house"},
      {done: false, description: "Wash the car"},
      {done: true, description: "Feed the dog"},
    ]

    this.signupForm = new FormGroup({
      name: new FormControl('', Validators.required),
      email: new FormControl('', [Validators.required, Validators.email]),
      password: new FormControl('', [Validators.required, Validators.minLength(6), Validators.maxLength(20)]),
      passwordConfirmation: new FormControl('', [Validators.required, Validators.minLength(6), Validators.maxLength(20)])
    });
  }

  changeTask(task: Task) {
    task.done = !task.done
  } 

  onSubmit() {
    console.log("New user added with success!")
    console.log(this.signupForm.value);

    this.router.navigate(["/app/add-user"])
  }

  onEdit() {
    this.router.navigate(["/app/edit-user"])
  }

  onDelete() {
    console.log("Excluindo usuário.")
  }

}

<div class="container-fluid">
  <div class="row align-items-left text-left">
    <div class="col content">
      <table class="table table-dark table-hover table-item text-center">
        <thead>
          <tr>
            <th scope="col">ID</th>
            <th scope="col">Nome</th>
            <th scope="col">E-mail</th>
            <th scope="col">Função</th>
            <th scope="col"></th>
          </tr>
        </thead>
        <tbody>
          <tr *ngFor="let task of taskList" [ngClass]="{'task-done': task.done}" (click)="changeTask(task)">
            <th scope="row"></th>
            <td>{{ task.description }}</td>
            <td>
              <input class="form-check-input" type="checkbox" [(ngModel)]="task.done" [checked]="task.done">
            </td>
            <th scope="row"></th>
            <th>
              <img width="30" height="30" src="https://img.icons8.com/badges/48/pencil.png" alt="pencil" (click)="onEdit()"/>
              <img width="30" height="30" src="https://img.icons8.com/ios-filled/50/FFFFFF/close-window.png" alt="close-window" (click)="onDelete()"/>
            </th>
          </tr>
        </tbody>
        <div class="d-flex justify-content-center">
          <button (click)="onSubmit()" class="btn btn">NEW</button>
        </div>
      </table>
    </div>
  </div>
</div>
